('2023-10-27 10:00:00', 'Alpha Centauri', 'Energy readings are stabilizing.');

-- Inserting sample data into SirianTranscript
INSERT INTO SirianTranscript (event_timestamp, communication_fragment, sender_species) VALUES
('2023-10-26 10:00:00', 'Warning: Gravitational anomaly detected.', 'Pleiadian'),
('2023-10-26 11:00:00', 'Encountered new entity near Sirius B.', 'Arcturian'),
